jQuery(function($) {
    $.fn.tooltip && $(".pi-tooltip").each(function() {
        var $el = $(this),
            size = $el.data("tooltipSize");
        $el.tooltip($el.hasClass("pi-tooltip-base") ? {
            template: '<div class="tooltip pi-base"><div class="tooltip-arrow"></div><div class="tooltip-inner"></div></div>',
            trigger: "hover",
            size: size
        } : {
            template: '<div class="tooltip"><div class="tooltip-arrow"></div><div class="tooltip-inner"></div></div>',
            trigger: "hover",
            size: size
        })
    })
});
jQuery(".form-js").submit(function() {
    var thisform = jQuery(this);
    jQuery('.required-error', thisform).remove();
    var name = jQuery("#name").val();
    var mail = jQuery("#mail").val();
    var phone = jQuery("#phone").val();

    var data = {
        'name': name,
        'mail': mail,
        'phone': phone
    };
    if (name == "") {
        jQuery("#name").after('<span class="form-description required-error">Required field.</span>');
    } else {
        jQuery("#name").parent().find('.required-error').remove();
    }
    if (mail == "") {
        jQuery("#mail").after('<span class="form-description required-error">Required field.</span>');
    } else {
        jQuery("#mail").parent().find('.required-error').remove();
    }
    if (phone == "") {
        jQuery("#phone").after('<span class="form-description required-error">Required field.</span>');
    } else {
        jQuery("#phone").parent().find('.required-error').remove();
    }



    if (name != "" && mail != "" && phone != "") {
        jQuery.post("contact_us.php", data, function(result) {
            if (result == "done") {
                thisform.prepend("<div class='alert-message success'><p><strong>Thank you " + name + "!</strong> We'll be in touch real soon .</p></div>");
                jQuery("#name").val("");
                jQuery("#mail").val("");
                jQuery("#phone").val("");

            }
        });
    }
    return false;
});

